/* custom scripts */
$(function() {
  $('.lazy').lazy();
});

$(".nav_main_ul>li").hover(function(){
$(this).addClass("active");
}, function(){
$(this).removeClass("active");
});
$(".nav_main_ul>li").hover(function(){
$(this).addClass("active");
$(".nav_main_ul>li.active .submenu>li").hover(function(){
$(this).addClass("active");
}, function(){
$(this).removeClass("active");
});
}, function(){
$(this).removeClass("active");
});

$('#navtrigger').click(function(){
  $(this).toggleClass("is-active");
  if ($(this).hasClass("is-active")) {
    navToggleIn();
  }else{
    navToggleOut();
  }
});
$(".nav-backdrop").click(function(){
  $('#navtrigger').removeClass("is-active");
  navToggleOut();
});

var menuWidth;
if (window.matchMedia('(max-width: 767px)').matches)
{
menuWidth = '250px';
}else{
menuWidth = '380px';
}
function navToggleIn(){
  var menuAnim = anime.timeline();
  menuAnim
  .add({
  targets: '.nav-backdrop',
  width: ['0%','100%'],
  duration: 300,
  easing: 'easeInQuart',
  }).add({
  targets: '.fs-menu',
  width: ['0px',menuWidth],
  duration: 500,
  easing: 'easeInQuart',
  }, '-=300').add({
  targets: '#headernav',
  opacity: [0,1]
});
}
function navToggleOut(){
  var menuAnim = anime.timeline();
  menuAnim
  .add({
  targets: '.nav-backdrop',
  width: ['100%','0%'],
  duration: 300,
  easing: 'easeInQuart',
  }).add({
  targets: '.fs-menu',
  width: [menuWidth,'0px'],
  duration: 300,
  easing: 'easeInQuart',
  }).add({
  targets: '#headernav',
  opacity: [1,0],
  duration: 300,
  }, '-=300')    ;
}

